// Log inÃ­cio e validar payload
const payload = $json.body || $json;

if (!payload.indicators || payload.indicators.length === 0) {
  throw new Error('Payload invÃ¡lido: indicators nÃ£o encontrado');
}

console.log(`[IBGE] Iniciando coleta de ${payload.indicators.length} indicadores`);
console.log(`[IBGE] Run ID: ${payload.orchestrator_run_id}`);

return [{
  ...payload,
  start_time: new Date().toISOString()
}];

// Construir URLs da API IBGE Sidra para cada indicador
const indicators = $json.indicators_to_collect;
const territory = $json.territory;

const apiCalls = [];

for (const indicator of indicators) {
  let url = indicator.api_endpoint;

  // Substituir placeholders
  url = url.replace('{ibge_code}', territory.ibge_code);

  apiCalls.push({
    indicator_code: indicator.code,
    indicator_name: indicator.name,
    territory_id: territory.id,
    territory_ibge: territory.ibge_code,
    territory_name: territory.name,
    url: url
  });
}

return apiCalls;

// Processar resposta da API IBGE Sidra
const response = $json;
const metadata = $('Build API URLs').item.json;

// Verificar se resposta Ã© vÃ¡lida
if (!response || typeof response !== 'object' || response.error) {
  console.warn(`[IBGE] âš ï¸  Erro na API para ${metadata.indicator_code} em ${metadata.territory_name}`);
  return [];
}

// API Sidra retorna array: [header, data, ...]
let data;
if (Array.isArray(response) && response.length > 1) {
  data = response[1]; // Segundo elemento tem os dados
} else if (Array.isArray(response) && response.length === 1) {
  return []; // Sem dados
} else {
  data = response;
}

// Extrair valor e ano
let value, year;

if (data.V) {
  value = parseFloat(data.V.toString().replace(',', '.'));
}

if (data.D3N) {
  year = parseInt(data.D3N);
} else if (data.D2N) {
  year = parseInt(data.D2N);
}

if (isNaN(value) || isNaN(year)) {
  console.warn(`[IBGE] âš ï¸  Dados invÃ¡lidos para ${metadata.indicator_code}`);
  return [];
}

console.log(`[IBGE] âœ“ ${metadata.territory_name}: ${metadata.indicator_code} = ${value} (${year})`);

return [{
  indicator_code: metadata.indicator_code,
  territory_id: metadata.territory_id,
  year: year,
  value: value,
  aggregation_method: 'raw',
  is_aggregated: false,
  data_quality: 'official',
  notes: `Coletado automaticamente via IBGE Sidra API em ${new Date().toISOString()}`
}];

// Atualizar dictionary apÃ³s coleta bem-sucedida
const insertedRecords = $input.all();

if (insertedRecords.length === 0) {
  return [];
}

// Agrupar por indicator_code
const indicatorUpdates = {};

for (const record of insertedRecords) {
  const data = record.json;
  const indicatorCode = $('Parse Response').item.json.indicator_code;
  const year = data.year;

  if (!indicatorUpdates[indicatorCode]) {
    indicatorUpdates[indicatorCode] = {
      code: indicatorCode,
      max_year: year
    };
  } else if (year > indicatorUpdates[indicatorCode].max_year) {
    indicatorUpdates[indicatorCode].max_year = year;
  }
}

return Object.values(indicatorUpdates);

// Consolidar resultados finais
const allInsertions = $('Upsert Indicator Value').all();
const dictionaryUpdates = $input.all();
const startTime = $('Validate Payload').first().json.start_time;
const endTime = new Date().toISOString();

const summary = {
  source: 'IBGE Sidra',
  start_time: startTime,
  end_time: endTime,
  duration_ms: new Date(endTime) - new Date(startTime),
  records_inserted: allInsertions.length,
  indicators_updated: dictionaryUpdates.length,
  status: 'completed',
  schema_version: 'territories_v2'
};

console.log('[IBGE] ðŸ“Š Resumo da Coleta:');
console.log(`  Registros inseridos: ${summary.records_inserted}`);
console.log(`  Indicadores atualizados: ${summary.indicators_updated}`);
console.log(`  DuraÃ§Ã£o: ${(summary.duration_ms / 1000).toFixed(2)}s`);
console.log(`  Schema: ${summary.schema_version} (territory_id)`);
console.log('[IBGE] âœ… Coleta concluÃ­da');

return [summary];
